import React, { useState } from "react";
import watch1 from "../assets/watch1.png";
import watch2 from "../assets/watch2.png";
import watch3 from "../assets/watch3.png";
import watch4 from "../assets/watch4.png";
import watch5 from "../assets/watch5.png";
import watch6 from "../assets/watch6.png";
import watch7 from "../assets/watch7.png";
import watch8 from "../assets/watch8.png";

const Products = () => {
  const watchImages = [watch1, watch2, watch3, watch4, watch5, watch6, watch7, watch8];
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState({ name: "", quantity: 1, imageUrl: null });
  const [editIndex, setEditIndex] = useState(null);

  const handleAddOrUpdate = (e) => {
    e.preventDefault();
    if (!newItem.imageUrl) {
      alert("Please select a watch image!");
      return;
    }
    const itemData = {
      name: newItem.name,
      quantity: newItem.quantity,
      imageUrl: newItem.imageUrl,
    };
    if (editIndex !== null) {
      const updatedItems = [...items];
      updatedItems[editIndex] = itemData;
      setItems(updatedItems);
      setEditIndex(null);
    } else {
      setItems([...items, itemData]);
    }
    setNewItem({ name: "", quantity: 1, imageUrl: null }); // Reset
  };
  
  const handleEdit = (index) => {
    const item = items[index];
    setNewItem({ name: item.name, quantity: item.quantity, imageUrl: item.imageUrl });
    setEditIndex(index);
  };
  
  const handleDelete = (index) => {
    const updatedItems = items.filter((_, i) => i !== index);
    setItems(updatedItems);
  };
  
  return (
    <div className="min-h-screen p-8 bg-gray-100">
      <h1 className="text-3xl font-bold text-gray-800 text-center">Watch Products</h1>
      <form onSubmit={handleAddOrUpdate} className="max-w-xl mx-auto mt-4 space-y-4 p-4 rounded-lg bg-white shadow">
        <input
          type="text"
          placeholder="Watch Name"
          value={newItem.name}
          onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
          className="w-full p-2 border rounded"
        />
        <input
          type="number"
          placeholder="Quantity"
          value={newItem.quantity}
          onChange={(e) => setNewItem({ ...newItem, quantity: e.target.value })}
          className="w-full p-2 border rounded"
        />

        <div className="grid grid-cols-4 gap-4 mt-4">
          {watchImages.map((img, index) => (
            <img
              key={index}
              src={img}
              alt={`Watch ${index + 1}`}
              onClick={() => setNewItem({ ...newItem, imageUrl: img })}
              className={`w-20 h-20 rounded cursor-pointer object-contain border-2 ${newItem.imageUrl === img ? 'border-blue-500' : 'border-gray-300'}`}
            />
          ))}
        </div>

        <button className="bg-blue-600 hover:bg-blue-700 text-white p-2 rounded w-full">
          {editIndex !== null ? "Update Watch" : "Add Watch"}
        </button>
      </form>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6 mt-8">
        {items.map((item, index) => (
          <div key={index} className="bg-white rounded-lg shadow p-4 text-center">
            <img
              src={item.imageUrl}
              alt={item.name}
              className="w-40 h-40 object-contain mx-auto rounded"
            />
            <h2 className="text-xl font-bold mt-2">{item.name}</h2>
            <p className="text-gray-600">Quantity: {item.quantity}</p>
            <div className="flex justify-center space-x-4 mt-4">
              <button
                onClick={() => handleEdit(index)}
                className="bg-yellow-500 hover:bg-yellow-600 text-white py-1 px-3 rounded"
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(index)}
                className="bg-red-600 hover:bg-red-700 text-white py-1 px-3 rounded"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
export default Products;
