import React, { useState } from "react";
import watch1 from "../assets/watch1.png";
import watch2 from "../assets/watch2.png";
import watch3 from "../assets/watch3.png";
import watch4 from "../assets/watch4.png";
import watch5 from "../assets/watch5.png";
import watch6 from "../assets/watch6.png";
import watch7 from "../assets/watch7.png";
import watch8 from "../assets/watch8.png";

const Products = () => {
  const [items, setItems] = useState([
    {
      name: "Golden Hour Sport",
      description: "Waterproof Women's Digital Chronograph",
      price: "₹1,199",
      rating: "4.3 out of 5 stars",
      imageUrl: watch1,
    },
    {
      name: "Titan Chic",
      description: "Premium Women's Analog Watch with Rose Gold Finish",
      price: "₹1,450",
      rating: "4.5 out of 5 stars",
      imageUrl: watch2,
    },
    {
      name: "Fastrack Breeze",
      description: "Sport Watch with Silicon Strap and Stopwatch",
      price: "₹1,099",
      rating: "4.1 out of 5 stars",
      imageUrl: watch3,
    },
    {
      name: "Sonata Elegance",
      description: "Classic Women's Watch with Leather Strap",
      price: "₹1,250",
      rating: "4.4 out of 5 stars",
      imageUrl: watch4,
    },
    {
      name: "Casio Chic",
      description: "Modern Women's Watch with Digital Chronograph",
      price: "₹1,325",
      rating: "4.2 out of 5 stars",
      imageUrl: watch5,
    },
    {
      name: "Timex Shine",
      description: "Lightweight Women's Watch with Stylish Dial",
      price: "₹1,099",
      rating: "4.0 out of 5 stars",
      imageUrl: watch6,
    },
    {
      name: "Fossil Glitter",
      description: "Stainless Steel Women's Watch with Crystal Details",
      price: "₹1,499",
      rating: "4.5 out of 5 stars",
      imageUrl: watch7,
    },
    {
      name: "Skagen Simplicity",
      description: "Ultra-thin Women's Watch with Mesh Band",
      price: "₹1,375",
      rating: "4.3 out of 5 stars",
      imageUrl: watch8,
    },
  ]);

  const [newWatch, setNewWatch] = useState({
    name: "",
    description: "",
    price: "",
    rating: "",
    imageUrl: "",
  });
  const [editIndex, setEditIndex] = useState(null);

  const handleInputChange = (e) => {
    setNewWatch({ ...newWatch, [e.target.name]: e.target.value });
  };
  
  const handleAdd = (e) => {
    e.preventDefault();
    if (!newWatch.name || !newWatch.description || !newWatch.price) {
      alert("Please fill out all fields");
      return;
    }
    if (editIndex !== null) {
      // Editing existing watch
      const updatedItems = items.map((item, index) =>
        index === editIndex ? newWatch : item
      );
      setItems(updatedItems);
      setEditIndex(null);
    } else {
      // Adding new watch
      setItems([...items, newWatch]);
    }
    setNewWatch({ name: "", description: "", price: "", rating: "", imageUrl: "" }); // Reset fields
  };
  
  const handleEdit = (index) => {
    setNewWatch(items[index]);
    setEditIndex(index);
  };
  
  const handleDelete = (index) => {
    const updatedItems = items.filter((_, i) => i !== index);
    setItems(updatedItems);
  };
  
  return (
    <div className="bg-gray-100 p-8 min-h-screen">
      <h1 className="text-4xl font-bold text-center text-gray-800">Product List</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mt-8">
        {items.map((item, index) => (
          <div
            key={index}
            className="bg-white rounded-2xl shadow-lg p-4 flex flex-col items-center hover:scale-105 transform transition duration-300"
          >
            {item.imageUrl && (
              <img
                src={item.imageUrl}
                alt={item.name}
                className="w-40 h-40 object-contain rounded-lg"
              />
            )}
            <h2 className="text-xl font-semibold mt-3">{item.name}</h2>
            <p className="text-gray-600 text-sm text-center mt-1">{item.description}</p>
            <p className="text-green-600 font-bold mt-2">{item.price}</p>
            <p className="text-yellow-600">{item.rating}</p>
            <div className="mt-4 flex space-x-2">
              <button
                onClick={() => handleEdit(index)}
                className="bg-yellow-500 text-white rounded-full px-4 py-2 hover:bg-yellow-600"
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(index)}
                className="bg-red-500 text-white rounded-full px-4 py-2 hover:bg-red-600"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* New or Edit Watch Form */}
      <div className="bg-white rounded-2xl p-6 mt-10 max-w-xl mx-auto shadow-lg">
        <h2 className="text-2xl font-bold text-gray-800">
          {editIndex !== null ? "Edit Watch" : "Add New Watch"}
        </h2>
        <form onSubmit={handleAdd} className="mt-4 flex flex-col space-y-3">
          <input
            type="text"
            name="name"
            placeholder="Watch Name"
            value={newWatch.name}
            onChange={handleInputChange}
            className="border rounded-lg p-2"
            required
          />
          <input
            type="text"
            name="description"
            placeholder="Description"
            value={newWatch.description}
            onChange={handleInputChange}
            className="border rounded-lg p-2"
            required
          />
          <input
            type="text"
            name="price"
            placeholder="Price"
            value={newWatch.price}
            onChange={handleInputChange}
            className="border rounded-lg p-2"
            required
          />
          <input
            type="text"
            name="rating"
            placeholder="Rating"
            value={newWatch.rating}
            onChange={handleInputChange}
            className="border rounded-lg p-2"
            required
          />
          <input
            type="text"
            name="imageUrl"
            placeholder="Image URL"
            value={newWatch.imageUrl}
            onChange={handleInputChange}
            className="border rounded-lg p-2"
            required
          />
          <button
            type="submit"
            className={`rounded-full px-4 py-2 ${
              editIndex !== null
                ? "bg-yellow-600 hover:bg-yellow-700"
                : "bg-blue-600 hover:bg-blue-700"
            } text-white`}
          >
            {editIndex !== null ? "Update Watch" : "Add Watch"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Products;
