import React, { useState } from "react";
import watch1 from "../assets/watch1.png";
import watch2 from "../assets/watch2.png";
import watch3 from "../assets/watch3.png";
import watch4 from "../assets/watch4.png";
import watch5 from "../assets/watch5.png";
import watch6 from "../assets/watch6.png";
import watch7 from "../assets/watch7.png";
import watch8 from "../assets/watch8.png";

const Products = () => {
  const [items, setItems] = useState([
    {
      name: "Watch 1",
      quantity: 1,
      imageUrl: watch1,
      description:
        "GOLDEN HOUR Waterproof Sport Women's Digital Chronograph Silicone Strap Watch",
    },
    {
      name: "Watch 2",
      quantity: 1,
      imageUrl: watch2,
      description:
        "GOLDEN HOUR Waterproof Sport Women's Digital Chronograph Silicone Strap Watch",
    },
    {
      name: "Watch 3",
      quantity: 1,
      imageUrl: watch3,
      description:
        "GOLDEN HOUR Waterproof Sport Women's Digital Chronograph Silicone Strap Watch",
    },
    {
      name: "Watch 4",
      quantity: 1,
      imageUrl: watch4,
      description:
        "GOLDEN HOUR Waterproof Sport Women's Digital Chronograph Silicone Strap Watch",
    },
    {
      name: "Watch 5",
      quantity: 1,
      imageUrl: watch5,
      description:
        "GOLDEN HOUR Waterproof Sport Women's Digital Chronograph Silicone Strap Watch",
    },
    {
      name: "Watch 6",
      quantity: 1,
      imageUrl: watch6,
      description:
        "GOLDEN HOUR Waterproof Sport Women's Digital Chronograph Silicone Strap Watch",
    },
    {
      name: "Watch 7",
      quantity: 1,
      imageUrl: watch7,
      description:
        "GOLDEN HOUR Waterproof Sport Women's Digital Chronograph Silicone Strap Watch",
    },
    {
      name: "Watch 8",
      quantity: 1,
      imageUrl: watch8,
      description:
        "GOLDEN HOUR Waterproof Sport Women's Digital Chronograph Silicone Strap Watch",
    },
  ]);

  // Watch click panna quantity increment
  const handleWatchClick = (index) => {
    const updatedItems = [...items];
    updatedItems[index].quantity += 1;
    setItems(updatedItems);
  };

  // Delete watch
  const handleDeleteWatch = (index) => {
    const updatedItems = items.filter((_, i) => i !== index);
    setItems(updatedItems);
  };
  
  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-center">Product List</h1>
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mt-6">
        {items.map((item, index) => (
          <div
            key={index}
            className="bg-white rounded-lg shadow p-4 flex flex-col items-center"
          >
            <img
              src={item.imageUrl}
              alt={item.name}
              className="w-32 h-32 object-contain rounded cursor-pointer hover:scale-105"
              onClick={() => handleWatchClick(index)}
            />
            <h2 className="text-lg font-bold mt-2">{item.name}</h2>
            <p>Quantity: {item.quantity}</p>
            <p className="text-gray-600 text-sm">{item.description}</p>
            <div className="mt-2">
              <button
                onClick={() => handleDeleteWatch(index)}
                className="bg-red-500 text-white rounded px-3 py-1 text-sm"
              >
                Delete
              </button>
            </div>
            <p className="text-xs text-gray-500 mt-1">
              (Click image to add quantity.)
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
