import React, { useState } from "react";
import watch1 from "../assets/watch1.png"; 
import watch2 from "../assets/watch2.png"; 
import watch3 from "../assets/watch3.png"; 
import watch4 from "../assets/watch4.png"; 
import watch5 from "../assets/watch5.png"; 
import watch6 from "../assets/watch6.png"; 

const Products = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [cart, setCart] = useState([]);
  const [items] = useState([
    { name: "GOLDEN HOUR Waterproof Sport Women's Watch", quantity: 10, imageUrl: watch1 },
    { name: "Classic Titan Ladies Watch", quantity: 15, imageUrl: watch2 },
    { name: "Rose Gold Elegant Watch", quantity: 8, imageUrl: watch3 },
    { name: "Modern Chronograph Watch", quantity: 5, imageUrl: watch4 },
    { name: "Luxury Diamond Watch", quantity: 3, imageUrl: watch5 },
    { name: "Casual Day Watch", quantity: 20, imageUrl: watch6 },
  ]);

  const addToCart = (item) => {
    setCart((prevCart) => {
      const existing = prevCart.find((cartItem) => cartItem.name === item.name);
      if (existing) {
        return prevCart.map((cartItem) =>
          cartItem.name === item.name
            ? { ...cartItem, quantity: cartItem.quantity + 1 }
            : cartItem
        );
      } else {
        return [...prevCart, { ...item, quantity: 1 }];
      }
    });
  };
  
  return (
    <div className="p-4">
      <h1 className="text-3xl font-bold text-center mt-4">Watch Products</h1>

      {/* SEARCH BAR */}
      <div className="mb-4 flex justify-center">
        <input
          type="text"
          placeholder="Search watches..."
          className="border rounded-lg p-2 w-64 focus:outline-none focus:ring-2 focus:ring-blue-500"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      {/* WATCH LIST */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
        {items
          .filter((item) =>
            item.name.toLowerCase().includes(searchQuery.toLowerCase())
          )
          .map((item, index) => (
            <div
              key={index}
              className="bg-white rounded-lg shadow p-4 flex flex-col items-center"
            >
              <img
                src={item.imageUrl}
                alt={item.name}
                className="w-32 h-32 object-cover rounded"
              />
              <h2 className="text-xl font-bold mt-2">{item.name}</h2>
              <p className="text-gray-600">Quantity: {item.quantity}</p>
              <button
                onClick={() => addToCart(item)}
                className="mt-2 px-4 py-2 rounded bg-green-600 text-white hover:bg-green-700"
              >
                Add to Cart
              </button>
            </div>
        ))}
      </div>

      {/* CART */}
      <div className="mt-8 p-4 rounded-lg bg-gray-100">
        <h2 className="text-2xl font-bold">Your Cart</h2>
        {cart.length === 0 && <p className="text-gray-600">Your cart is empty</p>}

        {cart.length > 0 && (
          <div>
            <ul>
              {cart.map((cartItem, index) => (
                <li key={index} className="flex justify-between mt-2">
                  <span>{cartItem.name}</span>
                  <span>Qty: {cartItem.quantity}</span>
                </li>
              ))}
            </ul>
            <div className="mt-4 font-bold">
              Total Items: {cart.reduce((sum, item) => sum + item.quantity, 0)}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Products;
