import React, { useState } from "react";
import watch1 from "../assets/watch1.png";
import watch2 from "../assets/watch2.png";
import watch3 from "../assets/watch3.png";
import watch4 from "../assets/watch4.png";
import watch5 from "../assets/watch5.png";
import watch6 from "../assets/watch6.png";

const Products = () => {
  const [items, setItems] = useState([
    {
      name: "Golden Hour Sport",
      description: "Waterproof Women's Digital Chronograph with Silicone Strap",
      price: "₹1,199",
      rating: "4.3 out of 5 stars",
      imageUrl: watch1,
    },
    {
      name: "Titan Chic",
      description: "Premium Women's Analog Watch with Rose Gold Finish",
      price: "₹1,450",
      rating: "4.5 out of 5 stars",
      imageUrl: watch2,
    },
    {
      name: "Fastrack Breeze",
      description: "Sport Watch with Silicon Strap and Stopwatch",
      price: "₹1,099",
      rating: "4.1 out of 5 stars",
      imageUrl: watch3,
    },
    {
      name: "Sonata Elegance",
      description: "Classic Women's Watch with Leather Strap",
      price: "₹1,250",
      rating: "4.4 out of 5 stars",
      imageUrl: watch4,
    },
    {
      name: "Casio Chic",
      description: "Modern Women's Watch with Digital Chronograph",
      price: "₹1,325",
      rating: "4.2 out of 5 stars",
      imageUrl: watch5,
    },
    {
      name: "Timex Shine",
      description: "Lightweight Women's Watch with Stylish Dial",
      price: "₹1,099",
      rating: "4.0 out of 5 stars",
      imageUrl: watch6,
    },
  ]);

  const handleEdit = (index) => {
    alert(`Edit item ${index}`);
  };
  
  const handleDelete = (index) => {
    const updatedItems = items.filter((_, i) => i !== index);
    setItems(updatedItems);
  };
  
  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold text-center">Product List</h1>
      <div className="flex flex-wrap justify-center gap-6 mt-6">
        {items.map((item, index) => (
          <div
            key={index}
            className="bg-white rounded-2xl shadow-lg p-4 flex flex-col items-center max-w-[250px]"
          >
            <img
              src={item.imageUrl}
              alt={item.name}
              className="w-40 h-40 object-contain rounded-lg"
            />
            <h2 className="text-lg font-semibold mt-3">{item.name}</h2>
            <p className="text-center mt-1 text-gray-600">{item.description}</p>
            <p className="text-green-600 font-bold mt-2">{item.price}</p>
            <p className="text-yellow-600">{item.rating}</p>
            <div className="mt-4 space-x-2">
              <button
                onClick={() => handleEdit(index)}
                className="bg-yellow-500 text-white rounded px-3 py-1"
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(index)}
                className="bg-red-500 text-white rounded px-3 py-1"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
