import React from "react";
import { Link } from "react-router-dom";
import { ShoppingBag } from "lucide-react"; // Make sure it's installed
import watchBG from "../assets/watch1.png"; // You can use any watch image

const Home = () => {
  return (
    <div
      className="min-h-screen flex flex-col justify-center items-center bg-gray-900 text-white relative"
      style={{
        backgroundImage: `url(${watchBG})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      {/* Overlay for contrast */}
      <div className="absolute inset-0 bg-black bg-opacity-60"></div>

      <div className="relative z-10 flex flex-col justify-center items-center text-center p-4">
        <h1 className="text-5xl font-bold mt-4">Welcome to the Product App</h1>
        <p className="text-lg mt-3 max-w-xl">
          Discover a premium range of watches, tailored just for you. Click below
          to explore and shop the best deals!
        </p>
        <Link to="/products">
          <button className="mt-6 flex items-center justify-center bg-blue-600 text-white rounded-full px-8 py-3 hover:bg-blue-700 transition">
            <ShoppingBag className="mr-2" /> View Products
          </button>
        </Link>
      </div>
    </div>
  );
};

export default Home;
