import React, { useState } from "react";
import watchImage from "../assets/product.png"; // Ensure this path is correct

const Products = () => {
  const [items, setItems] = useState([
    { name: "Mobile Watch", quantity: 112, imageUrl: watchImage },
    { name: "Laptop Watch", quantity: 1, imageUrl: watchImage },
  ]);

  const handleEdit = (index) => {
    alert(`Edit item at index ${index}`);
    // Add your edit logic here
  };
  
  const handleDelete = (index) => {
    const updatedItems = items.filter((_, i) => i !== index);
    setItems(updatedItems);
  };
  
  return (
    <div className="min-h-screen flex flex-col items-center justify-start p-4">
      <h1 className="text-3xl font-bold mt-4">Product List</h1>
      <div className="mt-4 flex flex-wrap justify-center gap-6">
        {items.map((item, index) => (
          <div
            key={index}
            className="bg-white rounded-lg shadow p-4 flex flex-col items-center w-64"
          >
            <img
              src={item.imageUrl}
              alt={item.name}
              className="w-40 h-40 object-contain rounded"
            />
            <h2 className="text-lg font-semibold mt-2">{item.name}</h2>
            <p className="text-gray-600">Quantity: {item.quantity}</p>
            <div className="mt-3 flex gap-2">
              <button
                onClick={() => handleEdit(index)}
                className="bg-yellow-500 text-white rounded px-3 py-1 hover:bg-yellow-600"
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(index)}
                className="bg-red-500 text-white rounded px-3 py-1 hover:bg-red-600"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
