import React, { useState, useEffect } from "react";

const Products = () => {
  const [items, setItems] = useState([]);
  const [name, setName] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [editId, setEditId] = useState(null);

  const API_URL = "http://localhost:5000/items"; // Ensure your backend is running

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    const res = await fetch(API_URL);
    const data = await res.json();
    setItems(data);
  };
  
  const addItem = async () => {
    if (editId) {
      await fetch(`${API_URL}/${editId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, quantity }),
      });
      setEditId(null);
    } else {
      await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, quantity }),
      });
    }
    setName("");
    setQuantity(1);
    fetchItems();
  };
  
  const deleteItem = async (id) => {
    await fetch(`${API_URL}/${id}`, {
      method: "DELETE",
    });
    fetchItems();
  };
  
  const editItem = (item) => {
    setEditId(item._id);
    setName(item.name);
    setQuantity(item.quantity);
  };
  
  return (
    <div className="min-h-screen flex flex-col items-center justify-start bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-gray-800 mt-4">Product List</h1>
      <div className="bg-white rounded-lg p-4 mt-4 w-full max-w-md shadow">
        <input
          className="w-full p-2 rounded border border-gray-300 mt-2"
          placeholder="Product Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="number"
          className="w-full p-2 rounded border border-gray-300 mt-2"
          placeholder="Quantity"
          value={quantity}
          onChange={(e) => setQuantity(parseInt(e.target.value))}
        />
        <button
          onClick={addItem}
          className={`w-full mt-3 p-2 rounded text-white ${editId ? "bg-yellow-500 hover:bg-yellow-600" : "bg-blue-500 hover:bg-blue-600"}`}
        >
          {editId ? "Update Item" : "Add Item"}
        </button>
      </div>
      <div className="bg-white rounded-lg p-4 mt-4 w-full max-w-md shadow">
        <h2 className="text-xl font-semibold text-gray-700">Items</h2>
        <ul className="divide-y divide-gray-200 mt-2">
          {items.map((item) => (
            <li key={item._id} className="flex justify-between items-center p-2">
              <span>{item.name} ({item.quantity})</span>
              <div className="space-x-2">
                <button
                  onClick={() => editItem(item)}
                  className="bg-yellow-500 hover:bg-yellow-600 text-white p-1 rounded"
                >
                  Edit
                </button>
                <button
                  onClick={() => deleteItem(item._id)}
                  className="bg-red-500 hover:bg-red-600 text-white p-1 rounded"
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>  
      </div>
    </div>
  );
};

export default Products;
